"use strict";var MongoClient=require("mongodb").MongoClient,url="mongodb://localhost:27017",dbName="szhmqd21";exports.findList=function(o,t,c){MongoClient.connect(url,{useNewUrlParser:!0},function(n,e){e.db(dbName).collection(o).find(t).toArray(function(n,o){e.close(),c(n,o)})})},exports.findOne=function(o,t,c){MongoClient.connect(url,{useNewUrlParser:!0},function(n,e){e.db(dbName).collection(o).findOne(t,function(n,o){e.close(),c(n,o)})})},exports.insertOne=function(o,t,c){MongoClient.connect(url,{useNewUrlParser:!0},function(n,e){e.db(dbName).collection(o).insertOne(t,function(n,o){e.close(),c(n,o)})})};